function initNotifications(){let e=document.getElementById("notificationMenu");notifications.forEach(t=>{let l=document.createElement("a");l.className="dropdown-item",l.href="#",l.innerHTML=`${t.message} <small class="text-muted">${t.time}</small>`,l.dataset.id=t.id,e.appendChild(l)})}function initMessages(){let e=document.getElementById("messageMenu");messages.forEach(t=>{let l=document.createElement("a");l.className="dropdown-item",l.href=t.link,l.innerHTML=`${t.message} <small class="text-muted">${t.time}</small>`,l.dataset.id=t.id,e.appendChild(l)})}document.addEventListener("DOMContentLoaded",()=>{initNotifications(),initMessages(),document.querySelectorAll(".toggle-sidebar").forEach(e=>{e.addEventListener("click",()=>{document.body.clientWidth>=992?document.body.classList.toggle("sidebar-hidden"):document.body.classList.toggle("sidebar-show")})}),document.querySelector(".toggle-mode").addEventListener("click",()=>{document.body.classList.toggle("dark-mode"),document.body.classList.toggle("light-mode")})});